mapper.select:
select m.* from module m ,role_module rm ,role r where m.status = 1
and rm.role_id in
#{role.id}
and rm.role_id in (-1)
and rm.role_id = r.id and r.status = 1 and rm.module_id = m.id;

select  from module where 1=1


  and gmt_created >= #{startTime}


  and gmt_created <= #{endTime}
