mapper.select:
select u.* from user u ,user_group ug where
u.status = 1

  and ug.group_id IN

    #{group.id}


and u.user_id = ug.user_id;

select  from user where 1=1


  and gmt_created >= #{startTime}


  and gmt_created <= #{endTime}

order by gmt_modified DESC

select user_id from user where 1=1


  and gmt_created >= #{startTime}


  and gmt_created <= #{endTime}

order by gmt_modified DESC

SELECT DISTINCT
u.id, u.username, u.user_id, u.fans_number, u.phone, u.gender, u.age, u.slogen, u.login_strategy,
u.head_image_url, u.head_image_id, u.theme, u.theme_id, u.creator, u.creator_code, u.gmt_created,
u.modifier, u.modifier_code, u.gmt_modified, u.name_cn, u.full_name_cn, u.name_en, u.full_name_en,
u.status, u.bak01, u.bak02, u.bak03, u.bak04,u.bak05
    FROM
    USER u,
    user_group ug,
    groups g
    WHERE
    g.`id` = #{groupsId}
    AND ug.group_id = g.`id`
    AND ug.`user_id` = u.`user_id`

  and u.id = #{id}


  and u.username = #{username}


  and u.password = #{password}


  and u.user_id = #{userId}


  and u.fans_number = #{fansNumber}


  and u.phone = #{phone}


  and u.gender = #{gender}


  and u.age = #{age}


  and u.slogen = #{slogen}


  and u.login_strategy = #{loginStrategy}


  and u.head_image_url = #{headImageUrl}


  and u.head_image_id = #{headImageId}


  and u.theme = #{theme}


  and u.theme_id = #{themeId}


  and u.creator = #{creator}


  and u.creator_code = #{creatorCode}


  and u.gmt_created >= #{gmtCreated}


  and u.modifier = #{modifier}


  and u.modifier_code = #{modifierCode}


  and u.gmt_modified = #{gmtModified}


  and u.name_cn = #{nameCn}


  and u.full_name_cn = #{fullNameCn}


  and u.name_en = #{nameEn}


  and u.full_name_en = #{fullNameEn}


  and u.status = #{status}


  and u.bak01 = #{bak01}


  and u.bak02 = #{bak02}


  and u.bak03 = #{bak03}


  and u.bak04 = #{bak04}


  and u.bak05 = #{bak05}


  and u.gmt_created >= #{startTime}


  and u.gmt_created <= #{endTime}

order by gmt_modified DESC
